import React, { useState,Component } from 'react'
import TodoDataService from '../../api/todo/TodoDataService.js'
import AuthenticationService from './AuthenticationService.js'
import HelloWorldService from '../../api/todo/HelloWorldService'
import moment from 'moment'
import { responsiveFontSizes } from '@material-ui/core'

class ListTodosComponent extends Component {

    constructor(props) {

        console.log('constructor')
        super(props)

        this.state = {
            todos: [{no:1,userId:'Hello',title:'Heading',body:'Lets Elaborate'},{no:2,userId:'Hello',title:'Heading',body:'Lets Elaborate'}],
            message: null,
            value: '',
            details:[],
            search:null,
            filteredData:[]
        }
        this.handleSuccessfulResponse = this.handleSuccessfulResponse.bind(this)
        this.refreshTodos = this.refreshTodos.bind(this)
        this.handleError = this.handleError.bind(this)
    }

    componentWillUnmount() {
        console.log('componentWillUnmount')
    }
    componentDidMount(){

        console.log('componentDIDUnmount')

        HelloWorldService.executeHelloWorldService()
        .then(response=>{
            console.log(response)
            this.setState({details:response.data})
            this.setState({filteredData:response.data})
            console.log(response.config.url)
            console.log(response.data);

        })
        // const filteredCountries = this.details.filter(country => {
        //     return country.name.toLowerCase().indexOf(this.search.toLowerCase()) !== -1;
        //   });
    }

    searchSpace=(event)=>{
        let keyword = event.target.value;

        this.setState({search:keyword})
        console.log("Searched"); 
        console.log(this.details);

        console.log(this.state.search)

              if(this.state.search=null){

              }
              else{
                 this.setState({filteredData:this.state.todos})
                 console.log("checking the search"); 
                 console.log(this.state.filteredData)

                 filteredData.title.toLowerCase().includes(this.state.search.toLowerCase())
                //  {this.state.filteredData.filter(filteredData => {
                //     return filteredData.title.toLowerCase().indexOf(this.state.search) > -1;





              }
      }
    // shouldComponentUpdate(nextProps, nextState) {
    //     console.log('shouldComponentUpdate')
    //     console.log(nextProps)
    //     console.log(nextState)
    //     return true
    // }

    // retrieveWelcomeMessage() {
    //     console.log("The new clicked")
    //     HelloWorldService.executeHelloWorldService()

    //         .then(response=>console.log(response))
    //         .catch()
    // }
    handleSuccessfulResponse(response) {
        console.log(response)
        this.setState({ welcomeMessage: response.data.message })
    }
    handleError(error) {

        console.log(error.response)

        let errorMessage = '';

        if (error.message)
            errorMessage += error.message

        if (error.response && error.response.data) {
            errorMessage += error.response.data.message
        }

        this.setState({ welcomeMessage: errorMessage })
    }
    // componentDidMount() {
    //     console.log('componentDidMount')
    //     this.refreshTodos();
    //     console.log(this.state)
    // }

    refreshTodos() {
        let username = AuthenticationService.getLoggedInUserName()
        TodoDataService.retrieveAllTodos(username)
            .then(
                response => {
                    console.log(response);
                    //this.setState({ todos: response.data })
                    console.log(this.state.todos.userId)
                }
            )
    }







    render(){
        console.log("inside the main render")
        console.log(this.state.details)
         return(
             <div>
                 <h3>List of Posts</h3>
                 {/* <div className="searchRow"> Filter Data as per Title:<input type="text"  onChange={this.handleChange} /></div> */}
                 <input type="text" placeholder="Enter item "  onChange={(e)=>this.searchSpace(e)} />
                 {/* <div className="container">
                    Click here to get a customized welcome message.
                    <button onClick={this.retrieveWelcomeMessage}
                        className="btn btn-success">Get Welcome Message</button>
                </div> */}
                 <table className="table">
                     <thead className="tableHeader">
                         <tr>
                             <th>No</th>
                             <th>UserId</th>
                             <th>Title</th>
                             <th>Description</th>
                         </tr>
                     </thead>
                     <tbody>
                         {
                             this.state.filteredData.map(
                                filteredData =>
                                <tr>
                                    <td>{filteredData.userId}</td>
                                    <td>{filteredData.id}</td>
                                    <td>{filteredData.title}</td>
                                    <td>{filteredData.body}</td>
                                </tr>
                             )

                         /* <tr>
                                <td>{this.state.details.url}</td>
                                <td>{this.state.todos.UserId}</td>
                                <td>{this.state.todos.Title}</td>
                                <td>{this.state.todos.Description}</td>
                         </tr> */}

                     </tbody>
                 </table>
             </div>
         )
    }
}

export default ListTodosComponent
