import React, { Component } from 'react'
import TodoDataService from '../../api/todo/TodoDataService.js'
import AuthenticationService from './AuthenticationService.js'
import HelloWorldService from '../../api/todo/HelloWorldService'
import moment from 'moment'

class ListTodosComponent extends Component {
    constructor(props) {
        console.log('constructor')
        super(props)
        this.state = {
            todos: {No:1,UserId:'Hello',Title:'Heading',Description:'Lets Elaborate'},
            message: null,
            value: ''
        }
        this.handleSuccessfulResponse = this.handleSuccessfulResponse.bind(this);
        this.refreshTodos = this.refreshTodos.bind(this);
        this.handleError = this.handleError.bind(this)
    }

    componentWillUnmount() {
        console.log('componentWillUnmount')
    }

    shouldComponentUpdate(nextProps, nextState) {
        console.log('shouldComponentUpdate')
        console.log(nextProps)
        console.log(nextState)
        return true
    }

    retrieveWelcomeMessage() {
        console.log("The new clicked")
        // HelloWorldService.executeHelloWorldService()
        // .then( response => this.handleSuccessfulResponse(response)

        //  )



        HelloWorldService.executeHelloWorldService()
            .then(response => this.handleSuccessfulResponse(response))
            .catch(error => this.handleError(error))
    }
    handleSuccessfulResponse(response) {
        console.log(response)
        this.setState({ welcomeMessage: response.data.message })
    }
    handleError(error) {

        console.log(error.response)

        let errorMessage = '';

        if (error.message)
            errorMessage += error.message

        if (error.response && error.response.data) {
            errorMessage += error.response.data.message
        }

        this.setState({ welcomeMessage: errorMessage })
    }
    componentDidMount() {
        console.log('componentDidMount')
        this.refreshTodos();
        console.log(this.state)
    }

    refreshTodos() {
        let username = AuthenticationService.getLoggedInUserName()
        TodoDataService.retrieveAllTodos(username)
            .then(
                response => {
                    //console.log(response);
                    this.setState({ todos: response.data })
                }
            )
    }







    render(){
         return(
             <div>
                 <h3>List of Posts</h3>
                 <div className="searchRow"> Filter Data as per Title:<input type="text"  onChange={this.handleChange} /></div>
                 <div className="container">
                    Click here to get a customized welcome message.
                    <button onClick={this.retrieveWelcomeMessage}
                        className="btn btn-success">Get Welcome Message</button>
                </div>
                 <table className="table">
                     <thead>
                         <tr>
                             <th>No</th>
                             <th>UserId</th>
                             <th>Title</th>
                             <th>Description</th>
                         </tr>
                     </thead>
                     <tbody>
                         <tr>
                                <td>{this.state.todos.No}</td>
                                <td>{this.state.todos.UserId}</td>
                                <td>{this.state.todos.Title}</td>
                                <td>{this.state.todos.Description}</td>
                         </tr>
                         <tr>
                         <td>{this.state.todos.No}</td>
                                <td>{this.state.todos.UserId}</td>
                                <td>{this.state.todos.Title}</td>
                                <td>{this.state.todos.Description}</td>
                         </tr>
                     </tbody>
                 </table>
             </div>
         )
    }
}

export default ListTodosComponent
